<?php

    // CREO UNA CLASSE NOMINATA MOVIE 
    class Movie {
        // ASSEGNO ALLA CLASSE MOVIE DELLE ISTANZE CON VARIABILI 
        public $titolo;
        public $genere;
        public $uscita;
        public $fasciaEta;
        public $poster;
        public $costoBiglietto = '10';
        public $sconto = 20;
        public $eta;

        // DICHIARO UN COSTRUTTORE PER ELABORARE DEI DATI CHE DEVONO ESSERE NECESSARIAMENTE INSERITI DALL'UTENTE 
        public function __construct($_titolo,$_genere,$_uscita,$_fasciaEta,$_poster){
            $this->titolo = $_titolo;
            $this->genere = $_genere;
            $this->uscita = $_uscita;
            $this->fasciaEta = $_fasciaEta;
            $this->poster = $_poster;
        }

        // DICHIARO DEI METODI DOVE IN BASE ALL'ETA' INSERITA DALL'UTENTE IL PREZZO DEL BIGLIETTO CAMBIA
        // METODO SET IMPOSTO DEI PARAMETRI 
        public function setCostoBiglietto($_eta){
            
            $this->eta = $_eta; 

            if($this->eta > 65 || $this->eta < 15){
                $this->costoBiglietto -= $this->costoBiglietto * $this->sconto / 100;
            }
        }

        // METODO GET RITORNO I PARAMETRI IMPOSTATI SUL SET
        public function getCostoBiglietto(){
            return $this->costoBiglietto;
        }

    }

    // ASSEGNO AD UNA VARIABILE LA MIA CLASSE Il signore degli anelli
    $film = new Movie('I signore degli anelli','Horror, Fantasy, Avventura',2005,'+14','data:image/jpeg;base64,');

    // LAVORO SULLA VARIABILE PER VEDERE IL RISULTATO
    if ($_SERVER["REQUEST_METHOD"] == "POST") {

        // Collegamento al mio input
        $eta = $_POST['fname'];

        // Se value input ha un valore 
        if (is_numeric($eta) && !empty($eta)) {
            $film->setCostoBiglietto($eta);
        }
    }
    // ASSEGNO AD UNA VARIABILE LA MIA CLASSE Il signore degli anelli fine
    
